#include <p32xxxx.h>.global Print_Ask_User.data.text.set noreorder.set User_Input, 0xA0000200.ent Print_Ask_UserPrint_Ask_User:li s0, 0or s0, s0, rala t0, User_Input                 # address of string -> t0Load_Char_Print_Ask_User:lb t2, 0(t0)                      # load char into t2beq t2, zero, Done_Print_Ask_User # t2 == 0 (NULL), done sending charsnop                               # otherwise more chars to sendbal Print_Print_Ask_User          # start print char™nopaddi t0, t0, 1                    # increment t0 pointerb Load_Char_Print_Ask_User        # load next charnopPrint_Print_Ask_User:lw t1, U1STA                      # (status) U1STA -> t1andi t1, t1, 0x200                # check transmit statusbne t1, zero, Print_Print_Ask_User # cannot transmit now, try againnop                               # otherwise continuesb t2, U1TXREG                    # char (t2) -> U1TXREGjr ra                             # return€™nopDone_Print_Ask_User:jr s0nop.end Print_Ask_User