# ************************************************************* ## ECE 344L - Microprocessors - Spring 2015## Name: Matthew Greci and Mitchell Jackson##     Laboratory Number: 6             Due Date: 7 May 2015### Lab Group: Matthew Greci and Mitchell Jackson### #*************************************************************** ## Print string to ask user for time.# #***************************************************************#include <p32xxxx.h>.global print_ask.data.text.set noreorder.set USER_INPUT, 0xA0000200.ent print_askprint_ask:li s0, 0or s0, s0, rala t0, USER_INPUT                 # address of string -> t0Load_Char_print_ask:lb t2, 0(t0)                      # load char into t2beq t2, zero, Done_print_ask # t2 == 0 (NULL), done sending charsnop                               # otherwise more chars to sendbal Print_print_ask          # start print char™nopaddi t0, t0, 1                    # increment t0 pointerb Load_Char_print_ask        # load next charnopPrint_print_ask:lw t1, U1STA                      # (status) U1STA -> t1andi t1, t1, 0x200                # check transmit statusbne t1, zero, Print_print_ask # cannot transmit now, try againnop                               # otherwise continuesb t2, U1TXREG                    # char (t2) -> U1TXREGjr ra                             # return€™nopDone_print_ask:jr s0nop.end print_ask